mapper.select:
SELECT p.v_productId,p.v_productName, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel1) v_categoryName1, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel2) v_categoryName2, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel3) v_categoryName3, p.v_store, p.v_imgUrl, p.v_grade, p.v_isDelete, (SELECT b.v_brandName FROM v_brand AS b WHERE b.v_brandId=p.v_brandId)v_brandName, p.v_createDate, p.v_modifyDate, p.v_sales_volume, p.v_commons_count FROM v_product AS p WHERE 1=1
and v_productName like concat('%',#{vProductName},'%')
LIMIT #{PageNo},#{pageSize}

SELECT p.v_productId,p.v_productName, v_categoryLevel1,v_categoryLevel2,v_categoryLevel3, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel1) v_categoryName1, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel2) v_categoryName2, (SELECT c.v_categoryName FROM v_category AS c WHERE c.v_categoryId=p.v_categoryLevel3) v_categoryName3, p.v_store, p.v_imgUrl, p.v_grade, p.v_isDelete, v_brandId, (SELECT b.v_brandName FROM v_brand AS b WHERE b.v_brandId=p.v_brandId)v_brandName, p.v_createDate, p.v_modifyDate, p.v_sales_volume, p.v_commons_count FROM v_product AS p WHERE 1=1
and v_productName like concat('%',#{vProductName},'%')
LIMIT #{PageNo},#{pageSize}
